using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.AI;public class police_movement_1 : MonoBehaviour{        private GameObject[] randomPoints;    private int currentPoint;    public Camera Cam;    public NavMeshAgent agent;    public GameObject target;    private void Start()    {        agent = this.GetComponent<NavMeshAgent>();        randomPoints = GameObject.FindGameObjectsWithTag("area");        //Debug.Log("randomPoints = " + randomPoints.Length.ToString());    }    // Update is called once per frame    private void Update()    {        float distance = Vector3.Distance(transform.position, target.transform.position);        if(distance < 25f)        {            agent.SetDestination(target.transform.position);        }        if (agent.hasPath == false)        {            currentPoint = Random.Range(0, randomPoints.Length + 1);            agent.SetDestination(randomPoints[currentPoint].transform.position);            //Debug.Log("moving to random point" + currentPoint.ToString());        }    }}